/**
 * Fake external library for testing namespace import resolution.
 * Exports types that will be accessed via `import * as extLib from "ext-lib"`.
 */

/** Network module configuration */
export interface INetworkModule {
    /** The base URL for network requests */
    baseUrl: string;
    /** Request timeout in milliseconds */
    timeout: number;
}

/** Authentication configuration */
export interface AuthConfig {
    /** The client ID */
    clientId: string;
    /** The tenant ID */
    tenantId: string;
    /** Scopes for the authentication */
    scopes: string[];
}

/** Logger interface from ext-lib */
export interface ExtLogger {
    /** Log an info message */
    info(message: string): void;
    /** Log an error message */
    error(message: string, err?: Error): void;
}

/** Token cache record */
export interface TokenCacheRecord {
    /** The access token */
    accessToken: string;
    /** The expiration timestamp */
    expiresOn: number;
}
